mapper.select:
		SELECT * FROM
			(SELECT D.*, T.NAME "temp_name", ROW_NUMBER() OVER(ORDER BY D.WRITE_DATE DESC) "ROWNUMBER" FROM DOCUMENT D, TEMPLATES T
				WHERE D.TEMP_CODE = T.CODE
				AND D.STATUS='TEMP'
	            AND D.WRITER_CODE=#{empCode}
	            AND D.WRITE_DATE BETWEEN #{startDate} AND #{endDate}
	            AND D.TEMP_CODE IN 

	            	#{item}

	            AND D.TITLE LIKE '%${searchText}%'
	        ORDER BY D.WRITE_DATE DESC)
        WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM (SELECT DOCUMENT.*, TEMPLATES.NAME "temp_name", EMPLOYEE.NAME "emp_name", DEPARTMENTS.NAME "departments_name", ROW_NUMBER() OVER(ORDER BY DOCUMENT.WRITE_DATE DESC) "ROWNUMBER" FROM DOCUMENT, TEMPLATES, DOC_CONFIRM, EMPLOYEE, DEPARTMENTS,(SELECT DOC_SEQ, SUM(CASE WHEN ISCONFIRM = 'Y' THEN 1 ELSE 0 END) AS "YCOUNT" FROM DOC_CONFIRM GROUP BY DOC_SEQ) D2 WHERE TEMPLATES.CODE = DOCUMENT.TEMP_CODE AND DOCUMENT.SEQ = DOC_CONFIRM.DOC_SEQ AND DOC_CONFIRM.APPROVER_CODE = EMPLOYEE.CODE AND EMPLOYEE.DEPT_CODE = DEPARTMENTS.CODE AND DOC_CONFIRM.APPROVER_ORDER = D2.YCOUNT+1 AND DOCUMENT.SEQ = D2.DOC_SEQ AND DOCUMENT.SEQ IN (SELECT D1.DOC_SEQ FROM DOC_CONFIRM D1 JOIN (SELECT DOC_SEQ, SUM(CASE WHEN ISCONFIRM = 'Y' THEN 1 ELSE 0 END) AS "YCOUNT" FROM DOC_CONFIRM GROUP BY DOC_SEQ) D2 ON D1.DOC_SEQ = D2.DOC_SEQ WHERE D1.APPROVER_ORDER = D2.YCOUNT+1) AND DOCUMENT.STATUS = 'RAISE' AND DOCUMENT.WRITER_CODE = #{empCode} AND DOCUMENT.WRITE_DATE BETWEEN #{startDate} AND #{endDate} AND DOCUMENT.TEMP_CODE IN
#{item}
AND DOCUMENT.TITLE LIKE '%${searchText}%' ORDER BY DOCUMENT.WRITE_DATE DESC) WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}

