	SELECT COUNT(*) FROM
		(SELECT D.*, T.NAME "temp_name" FROM DOCUMENT D, TEMPLATES T
		WHERE D.TEMP_CODE = T.CODE
			AND D.STATUS=#{status}
            AND D.WRITER_CODE=#{empCode}
            AND D.WRITE_DATE BETWEEN #{startDate} AND #{endDate}
            AND D.TEMP_CODE IN 

