mapper.select:
SELECT * 
	FROM (SELECT EMAIL.*, ROW_NUMBER() OVER(ORDER BY WRITE_DATE DESC) "ROWNUMBER" FROM EMAIL WHERE SENDER = #{email} AND RECEIVER = #{email}  AND STATUS='SEND')
	WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * 
	FROM (SELECT EMAIL.*, ROW_NUMBER() OVER(ORDER BY WRITE_DATE DESC) "ROWNUMBER" FROM EMAIL WHERE RECEIVER = #{email} AND SENDER != #{email} AND STATUS='SEND')
	WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * 
	FROM (SELECT EMAIL.*, ROW_NUMBER() OVER(ORDER BY WRITE_DATE DESC) "ROWNUMBER" FROM EMAIL WHERE SENDER = #{email} AND RECEIVER != #{email}  AND STATUS='SEND')
	WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * 
	FROM (SELECT EMAIL.*, ROW_NUMBER() OVER(ORDER BY WRITE_DATE DESC) "ROWNUMBER" FROM EMAIL WHERE RECEIVER = #{email} AND STATUS='DEL')
	WHERE ROWNUMBER BETWEEN #{startRowNum} AND #{endRowNum}
